                            onChange={(e) => setFormData({ 
                              ...formData, 
                              address: { ...formData.address, pincode: e.target.value } 
                            })}
                            required
                            data-testid="input-pincode"
                          />
                        </div>
                      </div>

                      <div className="grid grid-cols-2 gap-4">
