client/src/components/donor/DonationUplo.content:
import { useState } from 'react';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';
import { LoadingSpinner } from '@/components/shared/LoadingSpinner';
import { useAuth } from '@/contexts/AuthContext';
import { foodCategories } from '@shared/schema';
import { queryClient } from '@/lib/queryClient';
import { AlertCircle, Zap } from 'lucide-react';

interface DonationUploadModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function DonationUploadModal({ open, onOpenChange }: DonationUploadModalProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);
  const [formData, setFormData] = useState({
    category: '',
    name: '',
    quantity: '',
    unit: 'kg',
    preparationTime: '',
    expiryTime: '',
    specialInstructions: '',
    dietaryInfo: [] as string[],
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const res = await fetch('/api/donations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({
          donorId: user?.id,
          foodDetails: {
            category: formData.category,
            name: formData.name,
            quantity: parseFloat(formData.quantity),
            unit: formData.unit,
            preparationTime: formData.preparationTime,
            expiryTime: formData.expiryTime,
            dietaryInfo: formData.dietaryInfo,
            specialInstructions: formData.specialInstructions,
            images: [],
          },
          location: user?.donorProfile?.address || {
            address: { street: '', city: '', state: '', pincode: '' },
            coordinates: [0, 0],
          },
          status: 'pending',
        }),
      });

      const data = await res.json();

      if (!res.ok) {
        throw new Error(data.error || 'Failed to upload donation');
      }

      toast({
        title: 'Donation uploaded!',
        description: 'Your donation has been listed and will be matched with NGOs shortly.',
      });

      queryClient.invalidateQueries({ queryKey: ['/api/donations'] });
      queryClient.invalidateQueries({ queryKey: ['/api/donations/available'] });

      onOpenChange(false);
      setFormData({
        category: '',
        name: '',
        quantity: '',
        unit: 'kg',
        preparationTime: '',
        expiryTime: '',
        specialInstructions: '',
        dietaryInfo: [],
      });
    } catch (error) {
      toast({
        title: 'Upload failed',
        description: error instanceof Error ? error.message : 'Please try again',
        variant: 'destructive',
      });
    } finally {
      setIsLoading(false);
    }
  };

  const dietaryOptions = ['Vegetarian', 'Vegan', 'Contains Nuts', 'Gluten-free', 'Dairy-free'];

  // Calculate urgency score
  const calculateUrgency = (expiryTime: string) => {
    if (!expiryTime) return null;
    const now = new Date();
    const expiry = new Date(expiryTime);
    const hoursLeft = (expiry.getTime() - now.getTime()) / (1000 * 60 * 60);

    if (hoursLeft < 4) return { level: 'high', label: 'HIGH URGENCY - Critical', color: 'bg-red-500', icon: AlertCircle };
    if (hoursLeft < 12) return { level: 'medium', label: 'MEDIUM URGENCY', color: 'bg-yellow-500', icon: Zap };
    return { level: 'low', label: 'Low Urgency', color: 'bg-green-500', icon: Zap };
  };

  const urgency = calculateUrgency(formData.expiryTime);

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Upload Food Donation</DialogTitle>
          <DialogDescription>
            Provide details about the food you want to donate
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="category">Food Category</Label>
              <Select
                value={formData.category}
                onValueChange={(value) => setFormData({ ...formData, category: value })}
                required
              >
                <SelectTrigger data-testid="select-category">
                  <SelectValue placeholder="Select category" />
                </SelectTrigger>
                <SelectContent>
                  {foodCategories.map((cat) => (
                    <SelectItem key={cat} value={cat}>
                      {cat}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="name">Food Name</Label>
              <Input
                id="name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                required
                data-testid="input-food-name"
              />
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="quantity">Quantity</Label>
              <Input
                id="quantity"
                type="number"
                step="0.01"
                value={formData.quantity}
                onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
                required
                data-testid="input-quantity"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="unit">Unit</Label>
              <Select
                value={formData.unit}
                onValueChange={(value) => setFormData({ ...formData, unit: value })}
              >
                <SelectTrigger data-testid="select-unit">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="kg">Kilograms</SelectItem>
                  <SelectItem value="pieces">Pieces</SelectItem>
                  <SelectItem value="servings">Servings</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="preparationTime">Preparation Time</Label>
              <Input
                id="preparationTime"
                type="datetime-local"
                value={formData.preparationTime}
                onChange={(e) => setFormData({ ...formData, preparationTime: e.target.value })}
                required
                data-testid="input-prep-time"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="expiryTime">Expiry Time</Label>
              <Input
                id="expiryTime"
                type="datetime-local"
                value={formData.expiryTime}
                onChange={(e) => setFormData({ ...formData, expiryTime: e.target.value })}
                required
                data-testid="input-expiry-time"
              />
              {urgency && (
                <div className="flex items-center gap-2 mt-2">
                  <Badge className={`${urgency.color} text-white`} data-testid="badge-urgency">
                    {urgency.label}
                  </Badge>
                  {urgency.level === 'high' && (
                    <span className="text-xs text-red-600 font-medium">NGOs prioritize high-urgency donations</span>
                  )}
                </div>
              )}
            </div>
          </div>

          <div className="space-y-2">
            <Label>Dietary Information</Label>
            <div className="grid grid-cols-2 gap-4">
              {dietaryOptions.map((option) => (
                <div key={option} className="flex items-center space-x-2">
                  <Checkbox
                    id={option}
                    checked={formData.dietaryInfo.includes(option)}
                    onCheckedChange={(checked) => {
                      if (checked) {
                        setFormData({
                          ...formData,
                          dietaryInfo: [...formData.dietaryInfo, option],
                        });
                      } else {
                        setFormData({
                          ...formData,
                          dietaryInfo: formData.dietaryInfo.filter((item) => item !== option),
                        });
                      }
                    }}
                    data-testid={`checkbox-${option.toLowerCase().replace(/\s+/g, '-')}`}
                  />
                  <Label htmlFor={option} className="cursor-pointer">
                    {option}
                  </Label>
                </div>
              ))}
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="specialInstructions">Special Instructions (Optional)</Label>
            <Textarea
              id="specialInstructions"
              value={formData.specialInstructions}
              onChange={(e) => setFormData({ ...formData, specialInstructions: e.target.value })}
              placeholder="Any special handling or storage requirements..."
              rows={3}
              maxLength={500}
              data-testid="textarea-instructions"
            />
            <p className="text-xs text-muted-foreground">
              {formData.specialInstructions.length}/500 characters
            </p>
          </div>

          <div className="flex gap-3 justify-end">
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
              disabled={isLoading}
              data-testid="button-cancel"
            >
              Cancel
            </Button>
            <Button type="submit" disabled={isLoading} data-testid="button-submit">
              {isLoading ? <LoadingSpinner size="sm" /> : 'Upload Donation'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

shared/schema.ts.content:
import { pgTable, text, varchar, timestamp, integer, decimal, boolean, jsonb } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";
import { sql } from "drizzle-orm";

// User roles enum
export const userRoles = ["donor", "ngo", "volunteer", "admin"] as const;
export const donationStatuses = ["pending", "matched", "accepted", "in_transit", "delivered", "cancelled"] as const;
export const taskStatuses = ["assigned", "accepted", "picked_up", "in_transit", "delivered", "cancelled"] as const;
export const urgencyLevels = ["high", "medium", "low"] as const;
export const foodCategories = ["Cooked Meals", "Bakery", "Dairy", "Vegetables", "Fruits", "Packaged Food"] as const;

// Users table
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  fullName: text("full_name").notNull(),
  email: text("email").notNull().unique(),
  password: text("password").notNull(),
  role: text("role").notNull().$type<typeof userRoles[number]>(),
  phone: text("phone"),
  isVerified: boolean("is_verified").default(false),
  isActive: boolean("is_active").default(true),

  // Role-specific profiles stored as JSON
  donorProfile: jsonb("donor_profile").$type<{
    businessName: string;
    businessType: string;
    address: {
      street: string;
      city: string;
      state: string;
      pincode: string;
      coordinates: [number, number];
    };
    rating?: number;
    totalRatings?: number;
    ratingBreakdown?: {
      foodQuality: number;
      packaging: number;
      accuracy: number;
      communication: number;
    };
  }>(),

  ngoProfile: jsonb("ngo_profile").$type<{
    organizationName: string;
    registrationNumber: string;
    address: {
      street: string;
      city: string;
      state: string;
      pincode: string;
      coordinates: [number, number];
    };
    capacity: number;
    acceptedCategories: string[];
  }>(),

  volunteerProfile: jsonb("volunteer_profile").$type<{
    vehicleType: string;
    availability: string[];
    currentLocation: {
      coordinates: [number, number];
    };
    completedTasks: number;
  }>(),

  createdAt: timestamp("created_at").defaultNow(),
  lastLogin: timestamp("last_login"),
});

// Donations table
export const donations = pgTable("donations", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  donationId: text("donation_id").notNull().unique(),
  donorId: varchar("donor_id").notNull(),

  foodDetails: jsonb("food_details").notNull().$type<{
    category: string;
    name: string;
    quantity: number;
    unit: string;
    preparationTime: string;
    expiryTime: string;
    dietaryInfo: string[];
    specialInstructions?: string;
    images: string[];
  }>(),

  location: jsonb("location").notNull().$type<{
    address: {
      street: string;
      city: string;
      state: string;
      pincode: string;
    };
    coordinates: [number, number];
  }>(),

  urgencyScore: decimal("urgency_score"),
  urgencyCategory: text("urgency_category").$type<typeof urgencyLevels[number]>(),

  status: text("status").notNull().$type<typeof donationStatuses[number]>().default("pending"),
  completionPercentage: integer("completion_percentage").default(0),

  matchedNGOId: varchar("matched_ngo_id"),
  assignedVolunteerId: varchar("assigned_volunteer_id"),
  taskId: varchar("task_id"),

  timeline: jsonb("timeline").$type<Array<{
    status: string;
    timestamp: string;
    updatedBy?: string;
    note?: string;
  }>>(),

  cancellationReason: text("cancellation_reason"),

  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Volunteer tasks table
export const volunteerTasks = pgTable("volunteer_tasks", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  taskId: text("task_id").notNull().unique(),
  donationId: varchar("donation_id").notNull(),
  volunteerId: varchar("volunteer_id").notNull(),
  donorId: varchar("donor_id").notNull(),
  ngoId: varchar("ngo_id").notNull(),

  pickupLocation: jsonb("pickup_location").notNull().$type<{
    address: string;
    coordinates: [number, number];
  }>(),

  deliveryLocation: jsonb("delivery_location").notNull().$type<{
    address: string;
    coordinates: [number, number];
  }>(),

  distance: decimal("distance"),
  estimatedTime: integer("estimated_time"),

  status: text("status").notNull().$type<typeof taskStatuses[number]>().default("assigned"),

  pickupTime: timestamp("pickup_time"),
  deliveryTime: timestamp("delivery_time"),

  photos: jsonb("photos").$type<{
    pickupPhoto?: string;
    deliveryPhoto?: string;
  }>(),

  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Ratings table
export const ratings = pgTable("ratings", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  donationId: varchar("donation_id").notNull(),
  ratedBy: varchar("rated_by").notNull(),
  ratedTo: varchar("rated_to").notNull(),
  ratedType: text("rated_type").notNull().$type<"ngo" | "volunteer">(),

  rating: integer("rating").notNull(),
  comment: text("comment"),

  createdAt: timestamp("created_at").defaultNow(),
});

// Notifications table
export const notifications = pgTable("notifications", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  recipientId: varchar("recipient_id").notNull(),
  type: text("type").notNull(),

  title: text("title").notNull(),
  message: text("message").notNull(),

  relatedDonationId: varchar("related_donation_id"),
  relatedUserId: varchar("related_user_id"),

  isRead: boolean("is_read").default(false),

  createdAt: timestamp("created_at").defaultNow(),
});

// Insert schemas
export const insertUserSchema = createInsertSchema(users, {
  email: z.string().email(),
  role: z.enum(userRoles),
  phone: z.string().optional(),
}).omit({ id: true, createdAt: true, lastLogin: true });

export const insertDonationSchema = createInsertSchema(donations, {
  status: z.enum(donationStatuses).optional(),
  urgencyCategory: z.enum(urgencyLevels).optional(),
}).omit({ id: true, createdAt: true, updatedAt: true });

export const insertTaskSchema = createInsertSchema(volunteerTasks, {
  status: z.enum(taskStatuses).optional(),
}).omit({ id: true, createdAt: true, updatedAt: true });

export const insertRatingSchema = createInsertSchema(ratings).omit({ id: true, createdAt: true });

export const insertNotificationSchema = createInsertSchema(notifications).omit({ id: true, createdAt: true });

// Types
export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;

export type Donation = typeof donations.$inferSelect;
export type InsertDonation = z.infer<typeof insertDonationSchema>;

export type VolunteerTask = typeof volunteerTasks.$inferSelect;
export type InsertTask = z.infer<typeof insertTaskSchema>;

export type Rating = typeof ratings.$inferSelect;
export type InsertRating = z.infer<typeof insertRatingSchema>;

export type Notification = typeof notifications.$inferSelect;
export type InsertNotification = z.infer<typeof insertNotificationSchema>;

// Login schema
export const loginSchema = z.object({
  email: z.string().email("Invalid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
});

export type LoginInput = z.infer<typeof loginSchema>;

// Register schema with role-specific validation
export const registerSchema = z.object({
  fullName: z.string().min(2, "Name must be at least 2 characters"),
  email: z.string().email("Invalid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  phone: z.string().min(10, "Invalid phone number"),
  role: z.enum(userRoles),
  donorProfile: z.object({
    businessName: z.string(),
    businessType: z.string(),
    address: z.object({
      street: z.string(),
      city: z.string(),
      state: z.string(),
      pincode: z.string(),
      coordinates: z.tuple([z.number(), z.number()]),
    }),
  }).optional(),
  ngoProfile: z.object({
    organizationName: z.string(),
    registrationNumber: z.string(),
    address: z.object({
      street: z.string(),
      city: z.string(),
      state: z.string(),
      pincode: z.string(),
      coordinates: z.tuple([z.number(), z.number()]),
    }),
    capacity: z.number(),
    acceptedCategories: z.array(z.string()),
  }).optional(),
  volunteerProfile: z.object({
    vehicleType: z.string(),
    availability: z.array(z.string()),
    currentLocation: z.object({
      coordinates: z.tuple([z.number(), z.number()]),
    }),
    completedTasks: z.number().default(0),
  }).optional(),
});

export type RegisterInput = z.infer<typeof registerSchema>;

