import { useToast } from "@/hooks/use-toast"
import {
  Toast,
  ToastClose,
  ToastDescription,
  ToastProvider,
  ToastTitle,
  ToastViewport,
} from "@/components/ui/toast"

export function Toaster() {
  const { toasts } = useToast()

  return (
    <ToastProvider>
      {toasts.map(function ({ id, title, description, action, ...props }) {
        return (
          <Toast key={id} {...props}>
            <div className="grid gap-1">
              {title && <ToastTitle>{title}</ToastTitle>}
              {description && (
                <ToastDescription>{description}</ToastDescription>
              )}
            </div>
            {action}
            <ToastClose />
          </Toast>
        )
      })}
      <ToastViewport />
    </ToastProvider>
  )
}

client/src/hooks/useRealtimeUpdates.ts.content:
import { useEffect, useCallback } from 'react';
import { useAuth } from '@/contexts/AuthContext';
import { queryClient } from '@/lib/queryClient';
import { io, Socket } from 'socket.io-client';

let socket: Socket | null = null;

export function useRealtimeUpdates() {
  const { user } = useAuth();

  useEffect(() => {
    if (!user?.id) return;

    // Initialize socket connection if not already connected
    if (!socket || !socket.connected) {
      const protocol = window.location.protocol === 'https:' ? 'wss' : 'ws';
      const host = window.location.host;
      const token = localStorage.getItem('token');

      socket = io(window.location.origin, {
        secure: protocol === 'wss',
        reconnection: true,
        reconnectionDelay: 1000,
        reconnectionDelayMax: 5000,
        reconnectionAttempts: 5,
        auth: { token }
      });

      socket.on('connect', () => {
        console.log('Socket connected');
        socket?.emit('join_room', user.id);
      });

      socket.on('donation_accepted', (data: any) => {
        console.log('Donation accepted event (50%):', data);
        queryClient.invalidateQueries({ queryKey: ['/api/donations'] });
        queryClient.invalidateQueries({ queryKey: ['/api/donations/available'] });
        queryClient.invalidateQueries({ queryKey: ['/api/tasks'] });
      });

      socket.on('task_accepted', (data: any) => {
        console.log('Task accepted event (75%):', data);
        queryClient.invalidateQueries({ queryKey: ['/api/tasks'] });
        queryClient.invalidateQueries({ queryKey: ['/api/donations'] });
        queryClient.invalidateQueries({ queryKey: ['/api/donations/available'] });
      });

      socket.on('delivery_completed', (data: any) => {
        console.log('Delivery completed event (100%):', data);
        queryClient.invalidateQueries({ queryKey: ['/api/tasks'] });
        queryClient.invalidateQueries({ queryKey: ['/api/donations'] });
        queryClient.invalidateQueries({ queryKey: ['/api/donations/available'] });
      });

      socket.on('task_assigned', (data: any) => {
        console.log('Task assigned event:', data);
        queryClient.invalidateQueries({ queryKey: ['/api/tasks'] });
        queryClient.invalidateQueries({ queryKey: ['/api/donations/available'] });
      });

      socket.on('donation_rated', (data: any) => {
        console.log('Donation rated event:', data);
        queryClient.invalidateQueries({ queryKey: ['/api/donations'] });
        queryClient.invalidateQueries({ queryKey: ['/api/donations/available'] });
      });

      socket.on('notification_created', (data: any) => {
        console.log('New notification:', data);
        queryClient.invalidateQueries({ queryKey: ['/api/notifications'] });
      });

      socket.on('disconnect', () => {
        console.log('Socket disconnected');
      });
    }

    return () => {
      // Don't disconnect on unmount - keep connection alive
    };
  }, [user?.id]);

  return socket;
}

