import { Star, Award, Package, CheckCircle, MessageCircle } from 'lucide-react';
import { motion } from 'framer-motion';

interface DonorRatingDisplayProps {
  donor: any;
}

export function DonorRatingDisplay({ donor }: DonorRatingDisplayProps) {
  const rating = donor?.donorProfile?.rating || 0;
  const totalRatings = donor?.donorProfile?.totalRatings || 0;
  const isNewDonor = totalRatings === 0;
  const ratingBreakdown = donor?.donorProfile?.ratingBreakdown || {
    foodQuality: 0,
    packaging: 0,
    accuracy: 0,
    communication: 0,
  };

  const ratingPercentage = isNewDonor ? 0 : (rating / 10) * 100;

  const getStarColor = (rating: number) => {
    if (isNewDonor) return 'text-blue-600 dark:text-blue-400';
    if (rating >= 8) return 'text-emerald-600 dark:text-emerald-400';
    if (rating >= 6) return 'text-yellow-600 dark:text-yellow-400';
    if (rating >= 4) return 'text-orange-600 dark:text-orange-400';
    return 'text-red-600 dark:text-red-400';
  };

  const getQualityText = (rating: number) => {
    if (isNewDonor) return 'New Donor';
    if (rating >= 8) return 'Excellent';
    if (rating >= 6) return 'Good';
    if (rating >= 4) return 'Average';
    return 'Needs Improvement';
  };

  return (
    <div className="bg-card border border-border rounded-lg p-4">
      <div className="flex items-center gap-4 mb-4">
        <div className="text-center">
          <div className={`text-4xl font-bold ${getStarColor(rating)}`}>
            {isNewDonor ? 'NEW' : rating.toFixed(1)}
          </div>
          <div className="flex items-center justify-center mt-2">
            {[...Array(5)].map((_, i) => (
              <Star
                key={i}
                className={`w-4 h-4 ${
                  isNewDonor ? 'text-blue-300' : (i < Math.round(rating / 2) ? getStarColor(rating) : 'text-muted-foreground')
                } ${isNewDonor || i < Math.round(rating / 2) ? 'fill-current' : ''}`}
              />
            ))}
          </div>
          <p className="text-xs text-muted-foreground mt-1">
            {isNewDonor ? 'New to platform' : `${totalRatings} ${totalRatings === 1 ? 'rating' : 'ratings'}`}
          </p>
        </div>

        <div className="flex-1">
          <div className="h-3 bg-muted rounded-full overflow-hidden">
            <motion.div
              className={`h-full ${
                rating >= 8 ? 'bg-emerald-600' :
                rating >= 6 ? 'bg-yellow-600' :
                rating >= 4 ? 'bg-orange-600' :
                'bg-red-600'
              }`}
              initial={{ width: 0 }}
              animate={{ width: `${ratingPercentage}%` }}
              transition={{ duration: 0.8, ease: 'easeOut' }}
            />
          </div>
          <p className="text-xs text-muted-foreground mt-1">
            {getQualityText(rating)}
          </p>
        </div>
      </div>

      {totalRatings > 0 && (
        <div className="grid grid-cols-2 gap-3">
          <div className="flex items-center gap-2 text-sm">
            <Package className="w-4 h-4 text-emerald-600 dark:text-emerald-400 flex-shrink-0" />
            <div>
              <p className="text-xs text-muted-foreground">Food Quality</p>
              <p className="text-sm font-semibold">{ratingBreakdown.foodQuality.toFixed(1)}/10</p>
            </div>
          </div>

          <div className="flex items-center gap-2 text-sm">
            <Award className="w-4 h-4 text-emerald-600 dark:text-emerald-400 flex-shrink-0" />
            <div>
              <p className="text-xs text-muted-foreground">Packaging</p>
              <p className="text-sm font-semibold">{ratingBreakdown.packaging.toFixed(1)}/10</p>
            </div>
          </div>

          <div className="flex items-center gap-2 text-sm">
            <CheckCircle className="w-4 h-4 text-emerald-600 dark:text-emerald-400 flex-shrink-0" />
            <div>
              <p className="text-xs text-muted-foreground">Accuracy</p>
              <p className="text-sm font-semibold">{ratingBreakdown.accuracy.toFixed(1)}/10</p>
            </div>
          </div>

          <div className="flex items-center gap-2 text-sm">
            <MessageCircle className="w-4 h-4 text-emerald-600 dark:text-emerald-400 flex-shrink-0" />
            <div>
              <p className="text-xs text-muted-foreground">Communication</p>
              <p className="text-sm font-semibold">{ratingBreakdown.communication.toFixed(1)}/10</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/shared/CompletionB.content:
import { motion } from 'framer-motion';

interface CompletionBarProps {
  percentage: number;
  status: string;
}

export function CompletionBar({ percentage, status }: CompletionBarProps) {
  const getStatusLabel = (percent: number, stat: string) => {
    if (percent === 0) return 'Pending';
    if (percent === 50) return 'NGO Accepted - Awaiting Volunteer';
    if (percent === 75) return 'Volunteer Accepted';
    if (percent === 100) return 'Delivered';
    return stat;
  };

  const getBarColor = (percent: number) => {
    if (percent === 0) return 'bg-gray-300';
    if (percent <= 50) return 'bg-yellow-500';
    if (percent < 100) return 'bg-blue-500';
    return 'bg-green-600';
  };

  return (
    <div className="space-y-2">
      <div className="flex justify-between items-center">
        <span className="text-sm font-medium">{getStatusLabel(percentage, status)}</span>
        <span className="text-sm font-bold text-primary">{percentage}%</span>
      </div>
      <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
        <motion.div
          className={`h-full ${getBarColor(percentage)}`}
          initial={{ width: 0 }}
          animate={{ width: `${percentage}%` }}
          transition={{ duration: 0.6, ease: 'easeOut' }}
        />
      </div>
    </div>
  );
}

client/src/components/ngo/RatingModal.ts.content:
import { useState } from 'react';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { Star } from 'lucide-react';
import { LoadingSpinner } from '@/components/shared/LoadingSpinner';
import { queryClient, apiRequest } from '@/lib/queryClient';

interface RatingModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  donationId: string;
  donorId: string;
  donorName: string;
}

export function RatingModal({
  open,
  onOpenChange,
  donationId,
  donorId,
  donorName,
}: RatingModalProps) {
  const { toast } = useToast();
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      await apiRequest('POST', '/api/ratings', {
        donationId,
        donorId,
        rating,
        comment,
      });

      toast({
        title: 'Rating submitted!',
        description: `You rated ${donorName} with ${rating} stars.`,
      });

      queryClient.invalidateQueries({ queryKey: ['/api/donations'] });
      queryClient.invalidateQueries({ queryKey: ['/api/donations/available'] });
      queryClient.invalidateQueries({ queryKey: ['/api/tasks'] });

      onOpenChange(false);
      setRating(5);
      setComment('');
    } catch (error) {
      toast({
        title: 'Failed to submit rating',
        description: error instanceof Error ? error.message : 'Please try again',
        variant: 'destructive',
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>Rate {donorName}</DialogTitle>
          <DialogDescription>
            Share your experience with this donor to help us maintain quality standards
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="space-y-3">
            <Label>Quality Rating</Label>
            <div className="flex gap-2 justify-center py-4">
              {[1, 2, 3, 4, 5].map((star) => (
                <button
                  key={star}
                  type="button"
                  onClick={() => setRating(star)}
                  className="focus:outline-none transition-transform hover:scale-110"
                  data-testid={`button-rate-${star}`}
                >
                  <Star
                    className={`w-8 h-8 ${
                      star <= rating
                        ? 'fill-yellow-400 text-yellow-400'
                        : 'text-gray-300'
                    }`}
                  />
                </button>
              ))}
            </div>
            <div className="text-center text-sm text-muted-foreground">
              {rating === 5 && 'Excellent!'}
              {rating === 4 && 'Very Good'}
              {rating === 3 && 'Good'}
              {rating === 2 && 'Fair'}
              {rating === 1 && 'Poor'}
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="comment">Optional Comment</Label>
            <Textarea
              id="comment"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Share your experience... (max 300 characters)"
              maxLength={300}
              rows={3}
              data-testid="textarea-rating-comment"
            />
            <p className="text-xs text-muted-foreground">
              {comment.length}/300 characters
            </p>
          </div>

          <div className="flex gap-3 justify-end">
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
              disabled={isLoading}
              data-testid="button-cancel-rating"
            >
              Cancel
            </Button>
            <Button type="submit" disabled={isLoading} data-testid="button-submit-rating">
              {isLoading ? <LoadingSpinner size="sm" /> : 'Submit Rating'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

